import { useState } from 'react';
import { askQuestion } from '../api/api';
import { useNavigate } from 'react-router-dom';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

export default function AskQuestionForm() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [tags, setTags] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    await askQuestion({
      title,
      description,
      tags: tags.split(',').map(t => t.trim()),
    });
    navigate('/');
  };

  return (
    <div className="max-w-3xl mx-auto bg-white rounded-lg shadow p-8 mt-10">
      <h2 className="text-3xl font-bold mb-6 text-blue-700">Ask a New Question</h2>
      
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block mb-2 text-sm font-medium text-gray-700">Title</label>
          <input
            type="text"
            placeholder="e.g. How do I center a div in CSS?"
            className="w-full border border-gray-300 rounded px-4 py-2 focus:outline-none focus:ring focus:border-blue-300"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>

        <div>
          <label className="block mb-2 text-sm font-medium text-gray-700">Description</label>
          <ReactQuill
            theme="snow"
            value={description}
            onChange={setDescription}
            className="bg-white border rounded shadow-sm"
            placeholder="Explain your question in detail..."
          />
        </div>

        <div>
          <label className="block mb-2 text-sm font-medium text-gray-700">Tags (comma separated)</label>
          <input
            type="text"
            placeholder="e.g. javascript, css, html"
            className="w-full border border-gray-300 rounded px-4 py-2 focus:outline-none focus:ring focus:border-blue-300"
            value={tags}
            onChange={(e) => setTags(e.target.value)}
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-3 rounded hover:bg-blue-700 transition"
        >
          Submit Question
        </button>
      </form>
    </div>
  );
}
