// src/pages/HomePage.jsx

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import QuestionList from '../components/QuestionList';

const dummyQuestions = [
  {
    title: "How to join 2 columns in a data set to make a separate column in SQL",
    tags: ["SQL", "Beginner"],
    description: "I do not know the code for it as I am a beginner. As an example...",
    user: "User Name",
    answers: 5,
  },
  {
    title: "How to center a div in CSS?",
    tags: ["CSS", "HTML"],
    description: "I need help centering a div horizontally and vertically.",
    user: "User123",
    answers: 3,
  }
];

const HomePage = () => {
  const [questions, setQuestions] = useState(dummyQuestions);
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('Newest');
  const navigate = useNavigate();

  const getFiltered = () => {
    let filtered = [...questions];
    if (filter === 'Unanswered') {
      filtered = filtered.filter(q => q.answers === 0);
    }
    return filtered.filter(q =>
      q.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  const filteredQuestions = getFiltered();

  return (
    <div className="homepage">
      <div className="header">
        <h1>StackIt</h1>
        <button className="ask-button" onClick={() => navigate('/ask')}>Ask New Question</button>
        <div className="filters">
          <button onClick={() => setFilter("Newest")}>Newest</button>
          <button onClick={() => setFilter("Unanswered")}>Unanswered</button>
          <button>More â–¾</button>
        </div>
        <input
          className="search"
          placeholder="Search"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="login">Login</button>
      </div>

      <QuestionList questions={filteredQuestions} />

      <div className="pagination">
        <button>{"<"}</button>
        {[1,2,3,4,5,6,7].map(page => <button key={page}>{page}</button>)}
        <button>{">"}</button>
      </div>
    </div>
  );
};

export default HomePage;
