import { Link } from 'react-router-dom';

export default function QuestionCard({ question }) {
  return (
    <Link to={`/questions/${question._id}`} className="block border p-4 rounded hover:shadow">
      <h3 className="text-lg font-semibold">{question.title}</h3>
      <p className="text-gray-600 line-clamp-2" dangerouslySetInnerHTML={{ __html: question.description }} />
      <div className="mt-2">
        {question.tags.map(tag => (
          <span key={tag} className="inline-block bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded mr-2">{tag}</span>
        ))}
      </div>
    </Link>
  );
}
