import { useState } from 'react';
import { askQuestion } from '../api/api';
import { useNavigate } from 'react-router-dom';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

export default function AskQuestionForm() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [tags, setTags] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    await askQuestion({
      title,
      description,
      tags: tags.split(',').map((t) => t.trim())
    });
    navigate('/');
  };

  return (
    <div className="max-w-3xl mx-auto py-10 px-4">
      <h1 className="text-4xl font-bold text-gray-800 mb-6 border-b pb-3">
        üìù Ask a New Question
      </h1>

      <form
        onSubmit={handleSubmit}
        className="bg-white shadow-lg rounded-xl p-8 space-y-6 border border-gray-100"
      >
        <div>
          <label className="block mb-2 text-gray-700 font-semibold" htmlFor="title">
            Title
          </label>
          <input
            id="title"
            type="text"
            placeholder="E.g. How to join 2 columns in SQL?"
            className="w-full border border-gray-300 rounded-lg px-4 py-3 focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>

        <div>
          <label className="block mb-2 text-gray-700 font-semibold" htmlFor="description">
            Description
          </label>
          <ReactQuill
            theme="snow"
            value={description}
            onChange={setDescription}
            className="rounded-lg bg-white"
          />
        </div>

        <div>
          <label className="block mb-2 text-gray-700 font-semibold" htmlFor="tags">
            Tags
          </label>
          <input
            id="tags"
            type="text"
            placeholder="Comma-separated tags (e.g. SQL, Joins, Beginner)"
            className="w-full border border-gray-300 rounded-lg px-4 py-3 focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
            value={tags}
            onChange={(e) => setTags(e.target.value)}
            required
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 rounded-lg shadow-md transition duration-300"
        >
          Submit Question
        </button>
      </form>
    </div>
  );
}
